nxtblock.DeleteBlockUTXOs(block.Transactions)
nxtblock.ConvertBlockToUTXO(*block)

fmt.Print(nxtutxodb.UTXODatabase)

// * 8. Block in die Blockchain einfügen & Block an alle Peers senden

